set.seed(123)
split &lt;- initial_split(dat, prop = 0.8)
train &lt;- training(split)
test &lt;- testing(split)





library(DataExplorer)
plot_intro(train)
plot_missing(train)
plot_bar(train)
plot_histogram(train)
plot_correlation(train)


