  select(salary, .pred)

pred_dat &lt;- bind_rows(
  ols_pred |&gt; mutate(model = "ols"),
  ridge_pred |&gt; mutate(model = "ridge"),
  knn_pred |&gt; mutate(model = "knn"),
  rf_pred |&gt; mutate(model = "rf")
)

